import React from "react";
import { Box, Image, Text, Heading } from "theme-ui";
import { Link } from "components/link";
import classes from "./price-card.module.css";
import priceCheck from "assets/price-check.svg";
import priceCross from "assets/price-cross.svg";

const PriceCard = ({ data: { title, icon, amount, options, recommended } }) => {
  return (
    <Box
      className={`${
        recommended === null ? "priceCard " : "priceCard recommended"
      } ${classes.card}`}
      sx={styles.priceCard}
    >
      <Box sx={styles.priceHeader}>
        <Box className={`priceHeader ${classes.header}`}>
          {recommended === null ? null : (
            <Box sx={styles.recommended}>{recommended}</Box>
          )}
          <Image src={icon} alt={title} />
          <Heading as="h3">{title}</Heading>
        </Box>
      </Box>
      <Box as="ul">
        {options.map((option, index) => (
          <Box
            as="li"
            className={`${option.status === true ? "" : "closed"}`}
            key={index}
          >
            {option.status === true ? (
              <Image src={priceCheck} alt="" className={classes.image} />
            ) : (
              <Image src={priceCross} alt="" className={classes.image} />
            )}
            {option.label}
          </Box>
        ))}
      </Box>
      {/* <Box sx={styles.buttonWrap}>
        <Box className="priceAmount">
          <Text as="p">Starting from</Text>
          <Link path="#" sx={styles.heading}>
            {amount}
          </Link>
        </Box>
        <Link path="#" className="priceButton">
          <Image src={priceArrow} alt="price button" />
        </Link>
      </Box> */}
    </Box>
  );
};

export default PriceCard;

const styles = {
  priceCard: {
    px: ["20px", null, null, null, "40px"],
    border: "1px solid #E0E2E4",
    borderRadius: "10px",
    backgroundColor: "#ffffff",
    fontFamily: "DM Sans",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    ul: {
      p: 0,
      mt: ["30px", null, null, null, "50px"],
      listStyle: "none",
      li: {
        display: "flex",
        alignItems: "flex-start",
        color: "#343D48",
        fontSize: "16px",
        lineHeight: "26px",
        width: "100%",
        maxWidth: ["100%", null, null, null, "90%"],
        "+li": {
          mt: ["10px", null, null, null, "20px"]
        },
        "&.closed": {
          color: "rgba(52,61,72,.6)"
        },
        img: {
          flexShrink: 0,
          position: "relative",
          top: "5px",
          mr: "15px"
        }
      }
    }
  },
  priceHeader: {
    pt: ["40px", null, null, null, "60px"],
    position: "relative",
    display: "flex",
    justifyContent: "space-between",
    flexDirection: ["column", null, "row"],
    ".priceAmount": {
      textAlign: "right"
    },
    ".priceHeader": {
      mb: ["20px", null, "0"],
      h3: {
        lineHeight: 1,
        color: "#0F2137",
        fontSize: "22px",
        fontWeight: 700,
        letterSpacing: "-0.55px",
        mt: "35px",
        fontFamily: "DM Sans"
      }
    }
  },
  recommended: {
    display: "inline-block",
    backgroundColor: "primary",
    borderRadius: "3px",
    lineHeight: 1,
    color: "#ffffff",
    fontSize: "14px",
    fontWeight: 700,
    letterSpacing: "-0.14px",
    padding: "5px 8px",
    position: "absolute",
    top: ["-15px", null, null, null],
    left: "0px"
  },
  buttonWrap: {
    borderBottomLeftRadius: "10px",
    borderBottomRightRadius: "10px",
    py: "44px",
    mt: "60px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#F8F8FA",
    mx: ["-20px", null, null, null, "-40px"],
    px: ["20px", null, null, null, "40px"],
    img: {
      display: "block"
    },
    ".priceAmount": {
      p: {
        lineHeight: 1,
        fontSize: "16px",
        color: "#343D48",
        mb: "10px"
      }
    }
  },
  heading: {
    color: "primary",
    fontSize: "26px",
    fontWeight: 700,
    lineHeight: 1
  }
};
